    public static boolean isValid(String s) {
        if (s.length()<1 || s.length()>10000){
            return false;
        }
        String evaluateString="";
        for (int i =0;
        i < s.length();
        i++){
            if (isOpeningBrackets(s.charAt(i))) {
                // push the string
                evaluateString = evaluateString+s.charAt(i);
            } else if(evaluateString.length()!=0 &&
                    compareBrackets(evaluateString.charAt(evaluateString.length()-1),s.charAt(i))){
                // pop the string stack
                if (evaluateString.length()==1){
                    evaluateString = "";
                }
                else {
                    evaluateString = evaluateString.substring(0, evaluateString.length() - 1);
                }
            }
            else {
                return false;
            }
        }
        if (evaluateString.isEmpty()) {
            return true;
        }
        else {
            return false;
        }

    }

    private static boolean isOpeningBrackets(char charAt) {
        if (charAt == '('
        || charAt == '{'
        || charAt == '['){
            return true;
        }
        else {
            return false;
        }
    }

    public static boolean compareBrackets(Character left, Character right){
        if ((left == '{' && right == '}')
            || (left == '[' && right == ']')
            || (left == '(' && right == ')')){
            return true;
        }
        else
            return false;

    }
